<?phpnamespace jobs\Controllers;class Admin{    private $Admin;    private $jobTable;    private $staffTable;    public function __construct($Admin, $jobTable,$staffTable)    {        $this->Admin = $Admin;        $this->jobTable = $jobTable;        $this->staffTable = $staffTable;    }    public function list() {        $jobs = $this->jobTable->findAll();        return ['templates' => 'JobsList.html.php',            'title' => 'Job list',            'variables' => [                'jobs' => $jobs            ]        ];    }    public function staffList(){        $staff = $this->staffTable->findAll();        return ['templates' => 'StaffList.html.php',            'title' => 'All staff member',            'variables' => [                'staff' => $staff            ]        ];    }    public function delete() {        $this->jobTable->delete($_GET['id']);        header('location: /job/locationslist');    }    public function deleteStaff() {        $this->staffTable->delete($_GET['id']);        header('location: /Admin/staffList');    }    public function archive() {        $this->jobTable->archive($_GET['id']);        header('location: /Admin/list');    }    public function unarchive()    {        $this->jobTable->unarchive($_GET['id']);        header('location: /Admin/list');    }    public function grantAccess()    {        $table = 'user';        $column = 'Admin';        $this->staffTable->grantAccess($_GET['id']);        header('location: /Admin/stafflist');    }    public function removeAccess(){        $this->staffTable->removeAccess($_GET['id']);        header('location: /Admin/stafflist');    }    public function addUser(){        return ['templates' => 'addUser.html.php',            'title' => 'All user',            'variables' => [            ]        ];    }    public function edit(){    }}