<?phpnamespace jobs\Controllers;class Applicants{    private $applicantsTable;    private $jobTable;    public function __construct($applicantsTable,$jobTable)    {        $this->applicantsTable = $applicantsTable;        $this->jobTable = $jobTable;    }    public function applySubmit()    {        if (isset($_POST['submit'])) {            if ($_FILES['cv']['error'] == 0) {                $parts = explode('.', $_FILES['cv']['name']);                $extension = end($parts);                $fileName = uniqid() . '.' . $extension;                move_uploaded_file($_FILES['cv']['tmp_name'], 'cvs/' . $fileName);                $Applicant = [                    'name' => $_POST['name'],                    'email' => $_POST['email'],                    'details' => $_POST['details'],                    'jobId' => $_POST['jobId'],                    'cv' => $fileName                ];                $newApplicant = $this->applicantsTable->insert($Applicant);                $success = ' Your CV has been submitted';            } else {                $success = 'There was an error uploading your CV';            }        }        return ['templates' => 'submissionPage.html.php', 'title' => ' Job', 'variables' => ['success' => $success]];    }//    displays all of the applicants who have applied for a given job    public function applicantlist()    {        $variable1 = 'jobId';        $condition = '=';        $variable2 = 'id';        $applicants = $this->applicantsTable->findOtherJobs($variable1, $condition, $_GET['id']);        return ['templates' => 'applicantList.html.php', 'title' => ' Job', 'variables' => ['applicants' => $applicants]];    }    public function apply()    {        $variable1 = 'id';        $jobs = $this->jobTable->find($variable1, $_GET['id']);        return ['templates' => 'applicants.html.php', 'title' => ' Job', 'variables' => ['jobs' => $jobs]];    }}