<?phpnamespace jobs\Controllers;class Enquiry{    private $enquiry;    public function __construct($enquiry)    {        $this->enquiry = $enquiry;    }    public function enquiries()    {        $allEnquiries = $this->enquiry->findAll();        return ['templates' => 'allEnquiries.html.php',            'title' => 'All user',            'variables' => ['allEnquiries' => $allEnquiries            ]];    }    public function RespondToenquiries()    {        $this->isAdminLogged();        $this->isStaffLogged();        $this->isClientLogged();        $user = $this->enquiry->find('id', $_GET['id']);        return ['templates' => 'EnquiryResponse.html.php',            'title' => 'All user',            'variables' => ['user' => $user            ]];    }    public function RespondToenquiriesSubmit()    {        $this->isAdminLogged();        $this->isStaffLogged();        $this->isClientLogged();        if (isset($_POST['submit'])) {            $updateResponse =                ['id' => $_POST['id'],                    'name' => $_POST['name'],                    'email' => $_POST['email'],                    'EnquiryResponse' => $_POST['response'],                    'Staffid' => $_SESSION['userId'],                    'completed' => $_POST['completed']                ];            $response = $this->enquiry->update($updateResponse);        }        $success = 'Your enquiry has been added';        return ['templates' => 'submissionPage.html.php',            'title' => 'Add a job',            'variables' => ['success' => $success            ]        ];    }    public function contact()    {        return ['templates' => 'contact.html.php',            'title' => 'Add a job',            'variables' => [            ]        ];    }    public function contactSubmit()    {        if (isset($_POST['submit'])) {            $job = [                'name' => $_POST['name'],                'email' => $_POST['email'],                'telephone' => $_POST['telephone'],                'enquiry' => $_POST['enquiry'],                'userId' => $_SESSION['userId']            ];            $newJob = $this->enquiry->insert($job);        }        return ['templates' => 'contact.html.php',            'title' => 'Add a job',            'variables' => [            ]        ];    }}