<?phpnamespace jobs;class Routes implements \as2\Routes{    public function getPage($route)    {        require '../Database.php';        $jobTable = new \as2\DatabaseTable($pdo, 'job', 'id');        $categoriesTable = new \as2\DatabaseTable($pdo, 'category', 'id');        $userTable = new \as2\DatabaseTable($pdo, 'user', 'id');        $applicantsTable = new \as2\DatabaseTable($pdo, 'applicants', 'id');        $admin = new \as2\DatabaseTable($pdo, 'admin', 'id');        $staffTable = new \as2\DatabaseTable($pdo, 'staff', 'id');//        $enquiry = new \as2\DatabaseTable($pdo, 'enquiries', 'id');        $enquiry = new \as2\DatabaseTable($pdo, 'enquiries', 'id');        $controllers = [];        $controllers['job'] = new \jobs\Controllers\Job($jobTable,$applicantsTable,$enquiry);        $controllers['category'] = new \jobs\Controllers\Category($categoriesTable,$jobTable,$enquiry);        $controllers['User'] = new \jobs\Controllers\User($userTable, $jobTable,$enquiry,$categoriesTable);        $controllers['Admin'] = new \jobs\Controllers\Admin($admin, $jobTable, $staffTable);        if ($route == '') {            $page = $controllers['category']->home();        } else {            list($controllerName, $functionName) = explode('/', $route);            if ($_SERVER['REQUEST_METHOD'] == 'POST') {                $functionName = $functionName . 'Submit';            }            $controller = $controllers[$controllerName];            $page = $controller->$functionName();        }        return $page;    }    public function getDefaultRoute()    {        return 'Category/home';    }    public function checkLogin($route)    {        session_start();        $loginRoutes = [];        $loginRoutes['job/locationslist'] = true;        $requiresLogin = $loginRoutes[$route] ?? false;        if (($_SESSION['AdminLoggedIn']) == false) {            header('location: /user/login');            exit();        }    }}