<?php//namespace jobs\Controllers;//require 'as2/DatabaseTable.php';//require 'jobs/Controllers/Job.php';require 'jobs/Controllers/Applicants.php';class testApplicantFormTest extends \PHPUnit\Framework\TestCase {    private $controller;    public function setUp(){        $pdo = new \PDO('mysql:host=mysql;dbname=job;charset=utf8', 'student', 'student');        $applicantTable = new \as2\DatabaseTable($pdo, 'applicant', 'id');        $jobTable = new \as2\DatabaseTable($pdo, 'job', 'id');        $this->controller = new \jobs\Controllers\Applicants($applicantTable,$jobTable);    }    public function testValidateApply(){        $invalidUser = [            'name' => '',            'email' =>'',            'details' =>'',            'jobId' => '',            'cv' => ''        ];        $errors = $this->controller->testValidateApply($invalidUser);        $this->assertEquals(count($errors),0);    }    public function testApplyInvalidName(){        $invalidUser = [            'name' => '',            'email' =>'xyz',            'details' =>'xys',            'jobId' => '2',            'cv' => 'xyz'        ];        $errors = $this->controller->testApplyInvalidName($invalidUser);        $this->assertEquals(count($errors),1);    }    public function testApplyInvalidEmail(){        $invalidUser = [            'name' => 'Abz',            'email' =>'',            'details' =>'xys',            'jobId' => '2',            'cv' => 'xyz'        ];        $errors = $this->controller->testApplyInvalidEmail($invalidUser);        $this->assertEquals(count($errors),1);    }    public function testApplyInvalidCv(){        $invalidUser = [            'name' => 'Abz',            'email' =>'xyx',            'details' =>'',            'jobId' => '2',            'cv' => ''        ];        $errors = $this->controller->testApplyInvalidCv($invalidUser);        $this->assertEquals(count($errors),1);    }    }?>